import React, { useState } from 'react';

function App() {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const askQuestion = async () => {
    if (!question.trim()) return;

    setLoading(true);
    setError('');
    setResponse(null);

    try {
      const res = await fetch('http://127.0.0.1:8000/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question }),
      });

      if (!res.ok) {
        throw new Error('Backend error');
      }

      const data = await res.json();
      console.log('Backend response:', data);

      setResponse(data);
    } catch (err) {
      setError('‚ö†Ô∏è Failed to connect to backend.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üíä AI Supplement Advisor</h1>

      <input
        style={styles.input}
        type="text"
        placeholder="Ask a supplement question..."
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />

      <button style={styles.button} onClick={askQuestion}>
        Ask
      </button>

      {loading && <p>‚è≥ Thinking...</p>}
      {error && <p style={styles.error}>{error}</p>}

      {/* ‚úÖ SAFE RENDERING */}
      {response && typeof response === 'object' && (
        <div style={styles.card}>
          <h3>‚úÖ Answer</h3>
          <p>{String(response.answer)}</p>

          <h4>üìö Context Used</h4>
          <p style={styles.context}>
            {response.context_used
              ? String(response.context_used)
              : 'No context available'}
          </p>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '700px',
    margin: '50px auto',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
  },
  title: {
    marginBottom: '20px',
  },
  input: {
    width: '100%',
    padding: '12px',
    fontSize: '16px',
    marginBottom: '10px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    cursor: 'pointer',
  },
  card: {
    marginTop: '30px',
    padding: '20px',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
    textAlign: 'left',
  },
  context: {
    fontSize: '14px',
    color: '#555',
  },
  error: {
    color: 'red',
  },
};

export default App;
